$(document).ready(function(){$(".menu-link").click(function(e){e.preventDefault(),$(".mobile-nav").toggleClass("is-closed"),$(".menu").toggleClass("open")})}),$(window).scroll(function(){$(this).scrollTop()>$(".project-container").offset().top-$(window).height()/1.5&&$(".project-container figure").each(function(e){setTimeout(function(){$(".project-container figure").eq(e).addClass("is-showing")},150*(e+1))})});